import { Component, EventEmitter, OnInit, Input } from '@angular/core';
import { Route, Router } from '@angular/router';
import { ProductsService } from 'src/Service/products/products.service'
import { product } from '../../../models/product.model';
import { UploadImageComponent } from 'src/component/upload-image/upload-image.component'
import { HttpClient, HttpEventType, HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-add-product',
  templateUrl:'./add-product.component.html',
  styleUrls: ['./add-product.component.scss'],
  providers: [ProductsService]
})
export class AddProductComponent {
  progress!: number;
  message!: string;
  errorMessages: string[] = [];
  public onUploadFinished = new EventEmitter();
  //struct in the start
  addProductRequest: product = {
    id: '',
    image: '',
    name: '',
    price: 0,
    available: 0,
    read: ''
  }
  constructor(private http: HttpClient, private productService: ProductsService, private router: Router) { }

  ShowImageButton: boolean = true;



  addProduct() {//add product
    this.addProductRequest.id = '00000000-0000-0000-0000-000000000000';//needs a diffult id
    this.productService.addProduct(this.addProductRequest).subscribe({
      next: (product) => {//after go to product page
        this.router.navigate(['products']);
      },
            error: (respose) => {
              console.log(respose)
              this.errorMessages = [];
              this.errorMessages = respose.error.errors;
      }
    })
  }



  uploadFile = (files: any, name: string) => {
    if (files.length === 0) {
      return;
    }
    let fileToUpload = <File>files[0];
    const formData = new FormData();
    formData.append('file', fileToUpload, name + '.jpg');

    this.productService.PostImage(formData).subscribe({
      next: (event) => {
        if (event.type === HttpEventType.UploadProgress)
          this.progress = Math.round((100 * event.loaded) / event.total!);
        else if (event.type === HttpEventType.Response) {
          this.message = 'Upload success.';
          this.onUploadFinished.emit(event.body);
        }
      },
      error: (err: HttpErrorResponse) => console.log(err),
    });
  };

}
<div class="row">
  <div class="col-6">
    <form #form="ngForm" (ngSubmit)="addProduct()">
      <div class="mb-3">
        <label for="name" class="form-label">name</label>
        <input type="text" class="form-text" id="name"
               name="name" [(ngModel)]="addProductRequest.name">
      </div>
      <div class="mb-3">
        <label for="price" class="form-label">Price</label>
        <input type="text" class="form-text" id="price"
               name="price" [(ngModel)]="addProductRequest.price">
      </div>
      <div class="mb-3">
        <label for="available" class="form-label">available</label>
        <input type="text" class="form-text" id="available"
               name="available" [(ngModel)]="addProductRequest.available">
      </div>
      <div class="mb-3">
        <label for="read" class="form-label">read</label>
        <input type="text" class="form-text" id="read"
               name="read" [(ngModel)]="addProductRequest.read">
      </div>
      <div *ngIf="ShowImageButton" class="m-3">
        <h4>Upload Image</h4>

        <div class="mt-5">
          <div class="container-fluid">
            <input type="file"
                   #file
                   placeholder="Choose file"
                   (change)="uploadFile(file.files, productName)"
                   style="display: none" />

            <button type="button" class="btn btn-success" (click)="file.click()">Image</button>
          </div>
          <div class="col-md-4">
            <span class="upload" *ngIf="progress > 0"> {{ progress }}% </span>
            <span class="upload" *ngIf="message">
              {{ message }}
            </span>
          </div>
        </div>




        <!--<app-upload-image [productName]="addProductRequest.name"></app-upload-image>-->
      </div>
      <button type="submit" class="btn btn-primary">Submit</button>
      <div *ngIf="errorMessages.length > 0" class="alert alert-danger">
        <p><strong>Error(s) occurred:</strong></p>
        <ul>
          <li *ngFor="let errorMessage of errorMessages">{{ errorMessage }}</li>
        </ul>
      </div>
    </form>
 </div>
</div>
