import { Token } from '@angular/compiler';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UsersService } from 'src/Service/Users/users.service';

@Component({
  selector: 'app-log-in-line',
  templateUrl: './log-in-line.component.html',
  styleUrls: ['./log-in-line.component.scss']
})
export class LogInLineComponent implements OnInit {
  loggedIn: boolean = false;
  name: string = '';


  constructor(private router: Router, private usersService: UsersService) { }

  logout() {
    this.usersService.LogOut();
    this.name = 'guset'; // Reset the name to null
    this.loggedIn = false;
    localStorage.removeItem('userId');
  }


  ngOnInit() {
    this.usersService.IsUserAuthenticated();
    this.loggedIn = this.usersService.LogStatus;
    if (this.loggedIn == true) {
      const userId = localStorage.getItem('userId') ?? '';
      const tokenUser = this.usersService.getUserIdById(userId);
    }
  }
}
<p>
  hello to you {{name}}
  <a routerLink="log-In" *ngIf="loggedIn == false">Log In</a>
  <a href="/log-In" *ngIf="loggedIn" (click)="logout()">Log out</a>
</p>
