import { Component, OnInit } from '@angular/core';
import { user } from '../../../models/user.model';
import { UserStoreService } from '../../../Service/user-store/user-store.service';
import { UsersService } from '../../../Service/Users/users.service';



@Component({
  selector: 'app-users-admin',
  templateUrl: './users-admin.component.html',
  styleUrls: ['./users-admin.component.scss']
})
export class UsersAdminComponent implements OnInit {
  users: user[] = [];
  currentUser: user | null = null;
  constructor(private userService: UsersService, private userStoreService: UserStoreService) { }
  userid: string = this.userService.getIdfromToken();


  ngOnInit(): void {
    this.userStoreService.getAllUsers().subscribe(
      (data) => {
        this.users = data;
        console.log(this.userid);
        this.currentUser = this.users.find((u) => u.id === this.userid) || null;
      },
      (error) => {
        console.error('Error fetching users:', error);
      }
    );
  }
  editUser(user: user): void {

  }
  deleteUser(userId: string): void { }

}
<!-- users-admin.component.html -->
<div class="container">
  <h2>User Administration</h2>
  <table class="table table-bordered">
    <thead>
      <tr>
        <th>ID</th>
        <th>Email</th>
        <th>Name</th>
        <th>Last Name</th>
        <th>Admin</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let user of users">
        <td>{{ user.id }}</td>
        <td>{{ user.email }}</td>
        <td>{{ user.name }}</td>
        <td>{{ user.lastName }}</td>
        <td>{{ user.admin ? 'Yes' : 'No' }}</td>
        <td>
           <a [routerLink]="['/users-admin','edit',user.id]"><button class="btn btn-success">Edit User</button></a>
          <button class="btn btn-danger" (click)="deleteUser(user.id)">Delete</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
