import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CartItem } from '../../../models/CartItem.model';
import { product } from '../../../models/product.model';
import { CartService } from '../../../Service/cart/cart.service';
import { OrderService } from '../../../Service/order/order.service';
import { ProductsService } from '../../../Service/products/products.service';
import { UsersService } from '../../../Service/Users/users.service';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.scss']
})
export class CheckoutComponent implements OnInit {

  cartItems: product[] = [];
  UserItems: CartItem[] = [];
  totlePrice: number = 0;
  complete: boolean = false;
  completeAll: boolean = false;


  constructor(private cartService: CartService,
    private http: HttpClient,
    private productService: ProductsService,
    private userService: UsersService,
    private router: Router,
    private OrderService: OrderService
  ) { }


  ngOnInit(): void {
    this.completeAll = false;
    this.complete = false;
    const userId = this.userService.getIdfromToken();
    this.cartService.getCartItemsUser(userId).subscribe(item => {//again get cart ids
      console.log("Response:", item); // Log the  response
      this.UserItems = item;
      console.log(this.UserItems);
      this.productService.GetCartItem(this.UserItems).subscribe(items => {//print products
        this.cartItems = items
      });
    });
    this.OrderService.getPrice(userId).subscribe(price => {//get the total price
      this.totlePrice = price;
      console.log(price);
    })
    }
  resetcart(): void {
    this.complete = true;
  }

  completePayment() {
    this.complete = false;
    this.completeAll = true;
  }
}
<div *ngIf="!complete && !completeAll">
  <div *ngIf="cartItems.length === 0; else cartContents">
    <p>Your shopping cart is empty.</p>
  </div>


  <ng-template #cartContents id="cart-contents">
    <table>
      <thead>
        <tr>
          <th>Product Name</th>
          <th>Price</th>
          <th>Quantity</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of cartItems">
          <td>{{ item.name }}</td>
          <td>{{ item.price }}$</td>
          <td>1</td>
        </tr>
      </tbody>
    </table>
    <div class="pay">
      <h2 class="price">Total Price is: {{ totlePrice }}$</h2>
      <button  (click)="resetcart()">Pay Here</button> 
    </div>
  </ng-template>
</div>


<div *ngIf="complete">
  <form class="credit-card-form">
    <div class="form-group">
      <label for="cardNumber">Card Number</label>
      <input type="text" id="cardNumber" />
    </div>
    <div class="form-group">
      <label for="expirationDate">Date <br /></label>
      <input type="month" id="expirationDate" min="2024-01" />
    </div>
    <div class="form-group">
      <label for="cvv">CVV <br /></label>
      <input type="text" id="cvv" pattern="[0-9]{3}" title="Three-digit CVV" />
    </div>
    <button (click)="completePayment()">Complete Payment</button>
  </form>
</div>

<div *ngIf="completeAll" class="done">
  <h2>payment Completed</h2>
  <h3>ty for buying in gym-site</h3>
</div>
