import { Component, OnInit } from '@angular/core';
import { CartService } from 'src/Service/cart/cart.service';
import { product } from 'src/models/product.model';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/Environments/myEnvironments';
import { ProductsService } from '../../../Service/products/products.service';
import { UsersService } from '../../../Service/Users/users.service';
import { CartItem } from '../../../models/CartItem.model';

@Component({
  selector: 'app-shopping-cart',
  templateUrl: './shopping-cart.component.html',
  styleUrls: ['./shopping-cart.component.scss'],
  providers: [ProductsService]
})
export class ShoppingCartComponent implements OnInit {
  cartItems: product[] = [];
  UserItems: CartItem[] = [];
  baseApiUrl: string = environment.baseApiUrl;
  loggedIn: boolean = false;
  addCartRequest: CartItem = {
    CartId: '',
    userId: '',
    ProductId: '',
    Quantity: 1
  }

  constructor(private cartService: CartService,
    private http: HttpClient,
    private productService: ProductsService,
    private userService: UsersService) { }

  ngOnInit(): void {
    if (this.userService.LogStatus) {
      const userId = this.userService.getIdfromToken();
      this.cartService.getCartItemsUser(userId).subscribe(item => {
        console.log("Response:", item); // Log the entire response
        this.UserItems = item;
        console.log(this.UserItems);
        this.productService.GetCartItem(this.UserItems).subscribe(items => {
          this.cartItems = items
        });
      });

    } else {
      this.cartService.getCartItems().subscribe(items => {
        this.cartItems = items;
      });
    }
  }

  removeFromCart(productId: string): void {
    this.productService.updateAvailable(productId, 1).subscribe({
      next: (product) => {
        console.log(product);
      },
      error: (respose) => {
        console.log(respose)
      }
    })
    if (this.userService.LogStatus) {
      this.addCartRequest.CartId = '00000000-0000-0000-0000-000000000000'
      this.addCartRequest.ProductId = productId;
      this.addCartRequest.userId = this.userService.getIdfromToken();
      this.addCartRequest.Quantity = 1;
      this.cartService.removeFromCartUser(this.addCartRequest).subscribe()
    }
    this.cartService.removeFromCart(productId);
  }



  createOrder(): void {
    const orderData = {
      userId: 'user123', // You should replace this with the actual user ID when the user is logged in
      cartItems: this.cartItems,
    };

    if (this.loggedIn) {
      // User is logged in, send data to the API
      const orderApiUrl = this.baseApiUrl + '/create-order';

      this.http.post<any[]>(orderApiUrl, orderData).subscribe(
        (response: any[]) => {
          // Assuming your API returns an array of updated cart items, change 'any[]' to the actual response type
          this.cartService.updateCartInToken(response); // Update the token with the response
          this.cartService.clearCart(); // Clear the cart
        },
        (error) => {
          console.error('Failed to create the order:', error);
        }
      );
    } else {
      // User is not logged in, save data to local storage
      this.cartService.updateCartInToken(orderData.cartItems);
      this.cartService.clearCart();
    }
  }
}
<div *ngIf="cartItems.length === 0; else cartContents">
  <p>Your shopping cart is empty.</p>
</div>

<ng-template #cartContents>
  <table>
    <thead>
      <tr>
        <th>Product Name</th>
        <th>Price</th>
        <th>Quantity</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of cartItems">
        <td>{{ item.name }}</td>
        <td>{{ item.price }}</td>
        <td>1</td> 
        <td>
          <button (click)="removeFromCart(item.id)">Remove</button>
        </td>
      </tr>
    </tbody>
  </table>
  <button routerLink="/checkout">Proceed to Checkout</button> 
</ng-template>
