import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { user } from '../../../models/user.model';
import { UserStoreService } from '../../../Service/user-store/user-store.service';

@Component({
  selector: 'app-edit-user',
  templateUrl: './edit-user.component.html',
  styleUrls: ['./edit-user.component.scss']
})
export class EditUserComponent {
  userDetails: user = {
    id: '',
    name: '',
    lastName: '',
    email: '',
    admin: false,
    password:'',
  }
  constructor(private route: ActivatedRoute, private UserStoreService: UserStoreService, private router: Router) { }

  ngOnInIt(): void {
    this.route.paramMap.subscribe({//get by route the id
      next: (params) => {
        const id = params.get('id');

        if (id) {
          this.UserStoreService.getUser(id).subscribe({//going to the sql line with the id
            next: (response) => {
              this.userDetails = response;//put the ditels in the productDetails
            }
          })
        }
      }
    })
  }


}
<div class="row">
  <div class="col-6">
    <form #form="ngForm" (ngSubmit)="updateProduct()">
      <div class="mb-3">
        <label for="name" class="form-label">name</label>
        <input type="text" class="form-text" id="name"
               name="name" [(ngModel)]="productDetails.name">
      </div>
      <div class="mb-3">
        <label for="lastname" class="form-label">last name</label>
        <input type="text" class="form-text" id="lastname"
               name="price" [(ngModel)]="productDetails.lastname">
      </div>
      <div class="mb-3">
        <label for="email" class="form-label">email</label>
        <input type="text" class="form-text" id="email"
               name="email" [(ngModel)]="productDetails.email">
      </div>
      <div class="mb-3">
        <label for="admin" class="form-label">admin</label>
        <input type="radio" class="form-text" id="admin"
               name="admin" [(ngModel)]="productDetails.admin">
      </div>
      <button type="submit" class="btn btn-primary mr-5">save</button>
      <button type="button" class="btn btn-danger" (click)="deleteProduct(productDetails.id)">Delete</button>
    </form>
  </div>
</div>
