import { Component, OnInit } from '@angular/core';
import { ProductsService } from 'src/Service/products/products.service'
import { product } from 'src/models/product.model'
import { Router, ActivatedRoute } from '@angular/router'

@Component({
  selector: 'app-product-user',
  templateUrl: './product-user.component.html',
  styleUrls: ['./product-user.component.scss'],
  providers: [ProductsService]
})
export class ProductUserComponent {
  products: product[] = [];
  constructor(private productsService: ProductsService, private router: Router) { }


  ngOnInit() {
    this.productsService.getAllProducts().subscribe({//get all product int array in put it in products
      next: (products) => {
        this.products = products;
        this.loadProductImages();
        console.log(products);
      },
      error: (respose) =>
        console.log(respose),
    });
  }


  private loadProductImages(): void {
    console.log("loadproduct")
    this.products.forEach((product) => {
      console.log(product.id);
      this.productsService.GetImage(product.id).subscribe({
        next: (ImageData) => (product.image = URL.createObjectURL(ImageData)),
        error: (error) => console.error(error),
      })
      console.log(product.image);
    })
  }
}
<div class="add">
  <a href="AddProduct">Add Product</a>
</div>
<div *ngIf="products.length === 0">
  <a href="products"><app-starter-products></app-starter-products></a>
</div>
<div class="product-grid">
  <div class="product-item" *ngFor="let p of products; let i = index">
    <div class="product-image">
      <!--<img [src]="p.image" alt="{{ p.image }} Image" />-->
      <img [src]="p.image | safeResourceUrl"
           class="product.image"
           alt="no image" />

      <h4 class="product-name">Name: {{ p.name }}</h4>
      <div class="product-details">
        <div class="product-info">
          <span class="label">Details:</span>
          <span class="value">{{ p.read }}</span>
        </div>
        <div class="product-info">
          <span class="label">Price:</span>
          <span class="value">{{ p.price }}$</span>
          <span class="label">Available:</span>
          <span class="value">{{ p.available }}</span>
        </div>
      </div>
      <div class="button-group">
        <a [routerLink]="['/products','edit',p.id]"><button class="btn btn-success">Edit Product</button></a>
      </div>
    </div>
  </div>
