import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CartItem } from '../../../models/CartItem.model';
import { product } from '../../../models/product.model';
import { CartService } from '../../../Service/cart/cart.service';
import { OrderService } from '../../../Service/order/order.service';
import { ProductsService } from '../../../Service/products/products.service';
import { UsersService } from '../../../Service/Users/users.service';

@Component({
  selector: 'app-checkout',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.scss']
})
export class CheckoutComponent implements OnInit {

  cartItems: product[] = [];
  UserItems: CartItem[] = [];
  totlePrice: number = 0;
  complet: boolean = false;


  constructor(private cartService: CartService,
    private http: HttpClient,
    private productService: ProductsService,
    private userService: UsersService,
    private router: Router,
    private OrderService: OrderService
  ) { }


  ngOnInit(): void {
    const userId = this.userService.getIdfromToken();
    this.cartService.getCartItemsUser(userId).subscribe(item => {
      console.log("Response:", item); // Log the entire response
      this.UserItems = item;
      console.log(this.UserItems);
      this.productService.GetCartItem(this.UserItems).subscribe(items => {
        this.cartItems = items
      });
    });
    this.OrderService.getPrice(userId).subscribe(price => {
      this.totlePrice = price;
      console.log(price);
    })
    }
  resetcart(): void {
    this.complet = true;
  }

  completePayment() {  }





}
<div *ngIf="!complet">
  <div *ngIf="cartItems.length === 0; else cartContents">
    <p>Your shopping cart is empty.</p>
  </div>

  <ng-template #cartContents>
    <table>
      <thead>
        <tr>
          <th>Product Name</th>
          <th>Price</th>
          <th>Quantity</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of cartItems">
          <td>{{ item.name }}</td>
          <td>{{ item.price }}$</td>
          <td>1</td>
        </tr>
      </tbody>
    </table>
    <h2>total price is: {{totlePrice}}$</h2>
    input......
    <button (click)="resetcart()">Reset Cart</button>
  </ng-template>
</div>
<div *ngIf="complet">
  <form class="credit-card-form">
    <div class="form-group">
      <label for="cardNumber">Card Number</label>
      <input type="text" id="cardNumber" placeholder="1234 5678 9012 3456" />
    </div>
    <div class="form-group">
      <label for="expirationDate">Expiration Date</label>
      <input type="text" id="expirationDate" placeholder="MM/YY" />
    </div>
    <div class="form-group">
      <label for="cvv">CVV</label>
      <input type="text" id="cvv" placeholder="123" />
    </div>
    <button (click)="completePayment()">Complete Payment</button>
  </form>
</div>
