import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { NgForm } from '@angular/forms';
import { UsersService } from 'src/Service/Users/users.service';
import { user } from 'src/models/user.model';





@Component({
  selector: 'app-login-form',
  templateUrl: './login-form.component.html',
  styleUrls: ['./login-form.component.scss']
})
export class LoginFormComponent {
  invalidLogin: boolean = false;
  LoggedIn: boolean = false;
  myUser: user = {
    id: '',
    email: '',
    password: '',
    name: '',
    lastName: '',
    admin: false
  };

  // For Local Creds Saving
  constructor(private router: Router, private usersService: UsersService) { }

  ngOnInit(): void {
    this.usersService.IsUserAuthenticated();
    this.LoggedIn = this.usersService.LogStatus;
  }
  public UserAuthenticated() {
    this.usersService.IsUserAuthenticated();
  }

  async login(form: NgForm) {
    const credentials = {
      email: form.value.email,
      password: form.value.password,
    };

    this.myUser.email = credentials.email;
    this.myUser.password = credentials.password;


    await this.usersService.PostLogin(credentials).subscribe(
      (response) => {
        const token = (<any>response).token;
        localStorage.setItem('jwt', token);
        this.invalidLogin = false;
        if (this.usersService.IsUserAuthenticated()) {
          this.usersService.LogStatus = true;
          //this.router.navigate(['/log-In']);
          window.location.reload();
        }
      },
      (err) => {
        this.invalidLogin = true;
      }
    );
  }

}
<div class="container-xxl mt-5">
  <div *ngIf="!LoggedIn">
    <form class="form-signin" #loginForm="ngForm" (ngSubmit)="login(loginForm)">
      <div class="container-xxl mt-5">
        <h1 class="center">Login</h1>
        <div *ngIf="invalidLogin" class="alert alert-danger">
          Invalid name or password
        </div>
        <br />
        <div>
          <label for="email" class="sr-only">Email:</label>
          <input name="email"
                 type="text"
                 id="email"
                 ngModel
                 class="form-control"
                 placeholder="email"
                 required
                 autofocus />
        </div>
        <div>
          <label for="password" class="sr-only">Password:</label>
          <input name="password"
                 type="password"
                 id="password"
                 ngModel
                 class="form-control"
                 placeholder="password"
                 required
                 autofocus />
        </div>
        <br />
        <button class="btn btn-dark" type="submit">Login</button>
      </div>
    </form>
  </div>
  <div *ngIf="LoggedIn">
    <h1 class="center">You are logged in !</h1>
  </div>
</div>
<div *ngIf="LoggedIn == false">dont have a user, click <a href="sign-in">here</a> to creat</div>
