import { Component,OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { product } from '../../../models/product.model';
import { ProductsService } from '../../../Service/products/products.service';

@Component({
  selector: 'app-edit-product',
  templateUrl: './edit-product.component.html',
  styleUrls: ['./edit-product.component.scss'],
  providers: [ProductsService]

})
export class EditProductComponent {
  //struct when page load
  productDetails: product = {
    id: '',
    image: '',
    name: '',
    color: '',
    change: true,
    price: 0,
    available: 0,
    read: ''
  }
  constructor(private route: ActivatedRoute, private productService: ProductsService, private router: Router) {
    this.route.paramMap.subscribe({//get by route the id
      next: (params) => {
        const id = params.get('id');

        if (id) {
          this.productService.getProduct(id).subscribe({//going to the sql line with the id
            next: (response) => {
              this.productDetails = response;//put the ditels in the productDetails
            }
          })
        }
      }
    })
}

  ngOnInIt(): void {

  }

  updateProduct() {
    this.productService.updateProduct(this.productDetails.id, this.productDetails).subscribe({//go to server and update
      next: (product) => {
        this.router.navigate(['products']);//routet back to products page
      },
      error: (respose) => {
        console.log(respose)
      }
    })
  }

  deleteProduct(id: string) {
    this.productService.deleteProduct(id).subscribe({//go to server and delete
      next: (response) => {
        this.router.navigate(['products']);//back to product
      }
    })
  }
}
<div class="row">
  <div class="col-6">
    <form #form="ngForm" (ngSubmit)="updateProduct()">
      <div class="mb-3">
        <label for="name" class="form-label">name</label>
        <input type="text" class="form-text" id="name"
               name="name" [(ngModel)]="productDetails.name">
      </div>
      <div class="mb-3">
        <label for="color" class="form-label">color</label>
        <input type="text" class="form-text" id="color"
               name="color" [(ngModel)]="productDetails.color">
      </div>
      <div class="mb-3">
        <label for="change" class="form-label">changeable</label>
        <input type="checkbox" class="form-text" id="change"
               name="change" [(ngModel)]="productDetails.change">
      </div>
      <div class="mb-3">
        <label for="price" class="form-label">Price</label>
        <input type="text" class="form-text" id="price"
               name="price" [(ngModel)]="productDetails.price">
      </div>
      <div class="mb-3">
        <label for="available" class="form-label">available</label>
        <input type="text" class="form-text" id="available"
               name="available" [(ngModel)]="productDetails.available">
      </div>
      <div class="mb-3">
        <label for="read" class="form-label">read</label>
        <input type="text" class="form-text" id="read"
               name="read" [(ngModel)]="productDetails.read">
      </div>
      <button type="submit" class="btn btn-primary mr-5">save</button>
      <button type="button" class="btn btn-danger"(click)="deleteProduct(productDetails.id)">Delete</button>
    </form>
  </div>
</div>
