import { Component, OnInit } from '@angular/core';
import { CartService } from 'src/Service/cart/cart.service';
import { product } from 'src/models/product.model';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/Environments/myEnvironments';
import { ProductsService } from '../../../Service/products/products.service';

@Component({
  selector: 'app-shopping-cart',
  templateUrl: './shopping-cart.component.html',
  styleUrls: ['./shopping-cart.component.scss'],
  providers: [ProductsService]
})
export class ShoppingCartComponent implements OnInit {
  cartItems: product[] = [];
  baseApiUrl: string = environment.baseApiUrl; // API base URL
  loggedIn: boolean = false; // You should set this based on user login status

  constructor(private cartService: CartService, private http: HttpClient, private productService: ProductsService) { }

  ngOnInit(): void {
    this.cartService.getCartItems().subscribe(items => {
      this.cartItems = items;
    });
  }

  removeFromCart(productId: string): void {
    this.productService.updateAvailable(productId, -1);
    this.cartService.removeFromCart(productId);
  }

  clearCart(): void {
    this.cartService.clearCart();
  }

  createOrder(): void {
    const orderData = {
      userId: 'user123', // You should replace this with the actual user ID when the user is logged in
      cartItems: this.cartItems,
    };

    if (this.loggedIn) {
      // User is logged in, send data to the API
      const orderApiUrl = this.baseApiUrl + '/create-order';

      this.http.post<any[]>(orderApiUrl, orderData).subscribe(
        (response: any[]) => {
          // Assuming your API returns an array of updated cart items, change 'any[]' to the actual response type
          this.cartService.updateCartInToken(response); // Update the token with the response
          this.cartService.clearCart(); // Clear the cart
        },
        (error) => {
          console.error('Failed to create the order:', error);
        }
      );
    } else {
      // User is not logged in, save data to local storage
      this.cartService.updateCartInToken(orderData.cartItems);
      this.cartService.clearCart();
    }
  }
}
<div *ngIf="cartItems.length === 0; else cartContents">
  <p>Your shopping cart is empty.</p>
</div>

<ng-template #cartContents>
  <table>
    <thead>
      <tr>
        <th>Product Name</th>
        <th>Price</th>
        <th>Quantity</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of cartItems">
        <td>{{ item.name }}</td>
        <td>{{ item.price }}</td>
        <td>1</td>  You may implement quantity tracking in your service 
        <td>
          <button (click)="removeFromCart(item.id)">Remove</button>
        </td>
      </tr>
    </tbody>
  </table>
  <button (click)="clearCart()">Clear Cart</button>
  <button routerLink="/checkout">Proceed to Checkout</button>  Link to your checkout page 
</ng-template>
