import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { ProductsService } from '../../Service/products/products.service';
import {  HttpClient, HttpEventType, HttpErrorResponse}from '@angular/common/http'

@Component({
  selector: 'app-upload-image',
  templateUrl: './upload-image.component.html',
  styleUrls: ['./upload-image.component.scss']
})
export class UploadImageComponent implements OnInit {
  progress!: number;
  message!: string;
  @Output() public onUploadFinished = new EventEmitter();
  @Input() productName: string = '';
  constructor(private http: HttpClient, private productService: ProductsService) { }
  ngOnInit() { }


  uploadFile = (files: any, name: string) => {
    if (files.length === 0) {
      return;
    }
    let fileToUpload = <File>files[0];
    const formData = new FormData();
    formData.append('file', fileToUpload, name + '.jpg');

    this.productService.PostImage(formData).subscribe({
      next: (event) => {
        if (event.type === HttpEventType.UploadProgress)
          this.progress = Math.round((100 * event.loaded) / event.total!);
        else if (event.type === HttpEventType.Response) {
          this.message = 'Upload success.';
          this.onUploadFinished.emit(event.body);
          //location.reload();
        }
      },
      error: (err: HttpErrorResponse) => console.log(err),
    });
  };
}
<div class="mt-5">
  <div class="container-fluid">
    <input type="file"
           #file
           placeholder="Choose file"
           (change)="uploadFile(file.files, productName)"
           style="display: none" />
    <button type="button" class="btn btn-success" (click)="file.click()">
      Image
    </button>
  </div>
  <div class="col-md-4">
    <span class="upload" *ngIf="progress > 0"> {{ progress }}% </span>
    <span class="upload" *ngIf="message">
      {{ message }}
    </span>
  </div>
</div>
